// Can be accessed by Player and Background
static int level = 1;

struct Player
{
	void Reset()
	{
		xPos = SCREEN_WIDTH / 2;
		yPos = 4;
		isAccelerating = false;
		landerAcceleration = 0.0f;
		hasLanded = false;
		hasCrashed = false;
		crashTimer = 0.0f;
		landTimer = 0.0f;
		winTimer = 0.0f;
	}

	void Easy()
	{
		difficulty = "Easy";
		landerFuel = 120.0f;
		accelerationRate = 0.7f;
		decelerationRate = 0.1f;
		fuelConsumtionRate = 0.25f;
		level = 0;
	}

	void Medium()
	{
		difficulty = "Medium";
		landerFuel = 100.0f;
		accelerationRate = 0.5f;
		decelerationRate = 0.2f;
		fuelConsumtionRate = 0.5f;
		level = 1;
	}
	void Hard()
	{
		difficulty = "Hard";
		landerFuel = 50.0f;
		accelerationRate = 0.25f;
		decelerationRate = 0.3f;
		fuelConsumtionRate = 0.75f;
		level = 2;
	}

	float ConvertSpeed(float acceleration)
	{
		return acceleration *= 10.0f;
	}

	// Constants
	static const int WIDTH = 2;
	static const int HEIGHT = 3;
	const int CHARACTERS[WIDTH * HEIGHT] =
	{
		'/','\\',
		'|','|',
		'/','\\'
	};
	const int COLOURS[WIDTH * HEIGHT] =
	{
		0xF,0xF,
		0xF,0xF,
		0xE,0xE
	};

	// Difficulty Variables
	float accelerationRate = 0.5f;
	float decelerationRate = 0.2f;
	float fuelConsumtionRate = 0.5f;

	// Variables
	int xPos = SCREEN_WIDTH / 2;
	int yPos = 4;
	bool isAccelerating = false;
	float landerAcceleration = 0.0f;
	bool hasLanded = false;
	bool hasCrashed = false;
	float crashTimer = 0.0f;
	float landTimer = 0.0f;
	float landerFuel = 100.0f;
	int score = 0.0f;
	float winTimer = 0.0f;

	// strings
	std::string difficulty = "Medium";
};

